import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent  {
  lastValue: string = '';

  handleAddClick(value: string): void {
    this.lastValue = value;
  }

}

