import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{

  myVar=false;

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }
edit(){
  this.myVar=true;
}

 update(us:User){

  const i = this.newServis.users.findIndex(x=>x.name==us.name)

  this.newServis.users[i]=us;
  console.log(us)


 }
}
<h2>Users List</h2>
<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th>Password</th>
      <th>action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of newServis.users">
      <td><input *ngIf="myVar"> <span *ngIf="!myVar">{{ user.name }}</span></td>
      <td><input *ngIf="myVar"> <span *ngIf="!myVar">{{ user.phone }}</span></td>
      <td><input *ngIf="myVar"> <span *ngIf="!myVar">{{ user.password }}</span></td>
      <td>
        <button type="button" (click)="edit()">edit 
       *ngIf="myVar" type="button" (click)="update(user)">update</button>
      </td>


    </tr>
  </tbody>
</table>

