<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th>Password</th>
      <th *ngIf="myVar" >action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of newServis.users">
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>{{ user.password }}</td>
    </tr>
  </tbody>
</table>
