import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {



  newGroup: FormGroup;
    users: User[] = [];

    constructor(private fb: FormBuilder) {
        this.newGroup = this.fb.group({
            name: [''],
            phone: [''],
            password: ['']
        });
    }

    ngOnInit(): void {}

    addUser() {
        this.users.push(this.newGroup.value);
        console.log(this.newGroup.value.name);
    }

    updateUser() {
    const userData = this.newGroup.value;
    const existingUser = this.users.find(user => user.name === userData.name);

    if (existingUser) {
        this.newGroup.patchValue(existingUser);
    }  }

    saveUser() {
    const userData = this.newGroup.value;
    const existingUserIndex = this.users.findIndex(user => user.name === userData.name);

    if (existingUserIndex !== -1) {
        // Update existing user
        this.users[existingUserIndex] = userData;
    } else {
        // Add new user
        this.users.push(userData);
    }

    // Clear form after save/update
    this.newGroup.reset();
  }


  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}

    <h2>Users Form</h2>

    <form [formGroup]="newGroup" (ngSubmit)="addUser()">

        <label> Name:
        <input type="text" formControlName = "name"  required>
      </label>
      <br>
        <label> Phone:
        <input type="text" formControlName = "phone"  required>
      </label>
      <br>
        <label> Password:
        <input type="text" formControlName = "password"  required>
      </label>
      <br>
      <button type="submit"> Submit</button>
    </form>

    <app-shows [users]="users"></app-shows>


      <h2>Edit User</h2>
      <form (ngSubmit)="updateUser()">

        <div class="form-group">
          <label for="editName">Name:</label>
          <input type="text" formControlName ="name" required>
        </div>

        <div class="form-group">
          <label for="editPhone">Phone:</label>
          <input type="text" formControlName ="phone" required>
        </div>

        <div class="form-group">
          <label for="editPassword">Password:</label>
          <input type="text" formControlName ="password" required>
        </div>
        <button type="submit" class="btn btn-success">Update</button>
      </form>

 <h2>update users</h2>
    <table class="table">
      <thead>
        <tr>
          <th>Name</th>
          <th>Phone</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of users">
          <td>{{ user.name }}</td>
          <td>{{ user.phone }}</td>
          <td>
            <div class="btn-group" role="group">
              <button class="btn btn-primary" (click)="saveUser()">Edit</button>
              <button  (click)="deleteUser(user)">Delete</button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>


