import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  // @ViewChild('userForm') form: any;

  // public langs: User[]=[];

  newGroup: FormGroup;
    users: User[] = [];
    editingUser:User={name:"",phone:"",password:""};

    constructor(private fb: FormBuilder) {
        this.newGroup = this.fb.group({
            name: [''],
            phone: [''],
            password: ['']
        });
    }

    ngOnInit(): void {}

    addUser() {
        this.users.push(this.newGroup.value);
        console.log(this.newGroup.value.name);
    }

  editUser(user: User) {
    this.editingUser = { ...user };
  }

  updateUser() {
    const updatedUserIndex = this.users.findIndex(user => user.name === this.editingUser.name);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };
      this.editingUser = {
        name: "",
        phone: "",
        password: ""
      };
    }
  }


  // deleteUser(user: any) {
