import { Component } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
   form: any; 

  ngOnInit(){
this.form = new FormGroup({
  firstname:new FormControl("MODY"),
  lastname:new FormControl(""),
  languages:new FormControl("")
});
}

onSubmit(frm:string)
{
console.log(frm)
}
title = 'app works!' ;
inValide = true;


}

<form id ="form" (ngSubmit)="onSubmit(form.value)">
  <div class=" form-group">
    <label> first-name</label>
      <input type="text" formControlName="firstname"><br><BR>
      <label> last-name</label>
      <input type="text" formControlName="lastname"><br><BR>
      <label>language</label>
      <select name="languages" formControlName="languages">
      <option value="c#">c#</option>
      <option value="python">python</option>
      <option value="java">java</option>
      </select><BR><br>
      <input type="submit" value="submit">
  </div>
</form>
