import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Validator } from '@angular/forms';
import { ServiceService } from '../service.service';
import { find } from 'rxjs';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {


  newGroup!: FormGroup;
  hasError = false;



    constructor(private fb: FormBuilder,private newarr:ServiceService) {

    }

    ngOnInit() {
      this.newGroup = this.fb.group({

        name: [" ",Validators.required],
        phone: ['10', [Validators.required, Validators.pattern('[0-9]*')]],
        email: ["@ ",[Validators.required, Validators.email]],

      });
    }

    addUser() {
      if(this.newGroup.invalid){
        alert("not valid")
  } else
      this.newarr.users.push(this.newGroup.value);

    }



}

    <h2>Users Form</h2>

    <form [formGroup]="newGroup" (ngSubmit)="addUser()">


    <br><br>

    <label> 
            Name: 
            <br>

      <input type="text" formControlName = "name"  class="formControlName">
    </label>
    <br><br>



        <label>
              Phone:
              <br>
              <input  formControlName = "phone"   class="formControlName">
      </label>
     <br><br>



        <label>
              email:
              <br>
              <input type="text" formControlName = "email"   class="formControlName">
      </label>
      <br><br>



      <button type="submit" [disabled]="!newGroup.valid"> Submit</button>


  </form>

    <app-shows></app-shows>
