import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {



  newGroup: FormGroup;
    users: User[] = [];

    constructor(private fb: FormBuilder) {
        this.newGroup = this.fb.group({
            name: [''],
            phone: [''],
            password: ['']
        });
    }

    ngOnInit(): void {}

    addUser() {
        this.users.push(this.newGroup.value);
        console.log(this.newGroup.value.name);
    }

    updateUser() {
    const userData = this.newGroup.value;
    const existingUser = this.users.find(user => user.name === userData.name);

    if (existingUser) {
        this.newGroup.patchValue(existingUser);
    }  }

    saveUser() {
    const userData = this.newGroup.value;
    const existingUserIndex = this.users.findIndex(user => user.name === userData.name);

    if (existingUserIndex !== -1) {
        // Update existing user
        this.users[existingUserIndex] = userData;
    } else {
        // Add new user
        this.users.push(userData);
    }

    // Clear form after save/update
    this.newGroup.reset();
  }


  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
