import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {
  users: any[] = [];
  newUser: any = {};
  editingUser: any = null;

  ngOnInit(): void {
    this.users = [

      // ... רשימת המשתמשים הקיימים ...
    ];
  }

  addUser() {
    this.users.push(this.newUser);
    this.newUser = {};
  }

  editUser(user: any) {
    this.editingUser = { ...user };
  }

  updateUser() {
    // ניתן לכתוב כאן את הלוגיקה לעדכון המשתמש ברשימת המשתמשים
    // הנתונים של המשתמש שנערכו כבר ב-ngrx
    // לדוגמה, אם נניח שיש לך את המשתנה this.editingUser שמכיל את המשתמש שמתוכנן לעדכן
    const updatedUserIndex = this.users.findIndex(user => user.id === this.editingUser.id);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };
    }

    this.editingUser = null; // לאחר העדכון, לנקות את המשתנה המשמש לעריכה
  }

  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
