import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{

  myVar=false;

  editUser: User ={
    id:-1,
    name:"",
    phone:"",
    password:"",
  }

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }

  edit(user:User){
    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      password:user.password,
    }
    if(this.editUser.id == user.id){
      this.editUser.ClickIt=true;
    }
    else{
      this.editUser.ClickIt=false;

 }
    console.log(user,this.editUser);
  }

 save(user:User){
  console.log(user,this.editUser);
  this.myVar=false;
  const founduser = this.newServis.users.find(usr=>usr.id==user.id)
  if(founduser){
    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      password:user.password,
    }

  }    
  console.log(this.newServis.users);
  user.ClickIt=false;
  }

  cancel(user:User){
    user.ClickIt=false;
  }

}
