import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{

  isSameUser=true
  editUser: User ={
    id:-1,
    name:"",
    phone:"",
    email :"",
  }

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }

  edit(user:User,i:number){

    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      email:user.email,

  }
    if(this.editUser.id===user.id){
      this.isSameUser=true
     }
     this.isSameUser=false


    this.newServis.users[i].editMode=true;


  }

 save(user:User){
  console.log(user,this.editUser);
  const founduser = this.newServis.users.find(usr=>usr.id===user.id)
   if(founduser){
    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      email:user.email,
      editMode:user.editMode
    }
   }    
  // console.log(this.newServis.users,user.id,this.editUser.id);
  // 
}

  cancel(user:User){
    user.editMode=false;
    this.isSameUser=true
  }


  delete(u:User,i:number){
    if(this.newServis.users.find(usr=>usr.id===u.id)){
      this.newServis.users.slice(i,1)   
      console.log("the object: "+this.editUser,"the array: "+this.newServis.users.forEach((x)=>console.log(("the object is :"+x))))
      }
  }
}
