      <th>action</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of newServis.users">
      <td><input *ngIf="myVar" type="text" name="" id="phone"> <span *ngIf="myVar">{{ user.name }}</span></td>
