import { Component, OnInit } from '@angular/core';
import { User } from '../userInterface';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

   mygroup: FormGroup | undefined;
  users: User[] = [];
  newUser: User = {
    name: "",
    phone: "",
    password: ""
  };
  editingUser: User = {
    name: "",
    phone: "",
    password: ""
  };
  constuctor() {};

  ngOnInit() {
    this.mygroup = new FormGroup({
      name:new FormControl('', Validators.required),
      phone: new FormControl('', Validators.pattern('[0-9]{7,20}')),
      password: new FormControl('', Validators.required)
    });

  }

  addUser() {
    console.log("this.newUser: ",this.newUser)
    this.users.push(this.newUser);

    console.log("this.users: ",this.users)
  }

  editUser(user: User) {
    this.editingUser = { ...user };
  }

  updateUser() {
    const updatedUserIndex = this.users.findIndex(user => user.name === this.editingUser.name);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };

      this.editingUser = {
        name: "",
        phone: "",
        password: ""
      };
    }
  }

  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
};
