  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  Car :car= {
    model:' ',
    color:' '
  }

cars = [
  {model:'toyota'
  ,color:'blue'},

  {model:'nissan',
  color:'red'},

  {model:'honda',
  color:'green'}
]
