import { Component, EventEmitter, Input, OnInit, Output, ViewChild} from '@angular/core';
import { User } from '../userInterface';


type NewType = number;

@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent {

  @Output() user:EventEmitter<User> = new EventEmitter<User>();

  adduserrr(value:User){
this.user.emit(value)
  }




}
<h2>Users List</h2>
<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th>Password</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of users">
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>{{ user.password }}</td>
    </tr>
  </tbody>
</table>

<input #myout>
<button type="button" (click)="adduserrr(myout.value)"> clickMe </button>
