import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';
import { FormControl, FormGroup } from '@angular/forms';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  // @ViewChild('userForm') form: any;

  // public langs: User[]=[];
  newgroup = new FormGroup({
    name :new FormControl(''),
    phone : new FormControl(''),
    password : new FormControl('')
  })

  users: User[] = [

  ];

  newUser: User  ={
    name:"",
    phone:"",
    password:""
  };
  editingUser: User ={
    name:"",
    phone:"",
    password:""
  };

  ngOnInit(): void {
  }

  addUser() {
    if(this.form.valid){
      console.log('fororrrrrrrrrrrr',this.form)
    }else
    console.log('notValiddddddd',this.form)
    this.users.push(this.newUser);

  }

  editUser(user: User) {
    this.editingUser = { ...user };
  }

  updateUser() {
    const updatedUserIndex = this.users.findIndex(user => user.name === this.editingUser.name);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };
      this.editingUser = {
        name: "",
        phone: "",
        password: ""
      };
    }
  }


  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
