import { Component, OnInit, ViewChild } from '@angular/core';
import { User } from '../userInterface';



@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {


  users: User[] = [

  ];


  editingUser: User[] =[

  ];

  ngOnInit(): void {
  }

  addUser(newUser:User) {

    this.users.push(newUser);

  }

  editUser(user: User) {
    this.editingUser.push({...user});
  }

  updateUser() {

    const updatedUserIndex = this.users.find(f=>f.name===this.editingUser.values.prototype.name);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };
      this.editingUser = {
        name: "",
        phone: "",
        password: ""
      };
    }
  }


  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
