import { Component, OnInit } from '@angular/core';
import { ServiceService } from '../service.service';
import { User } from '../userInterface';

@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit {

  constructor(public newServis: ServiceService) {}

  editedUser: User = { name: '', phone: '', password: '' };
  selectedUserIndex: number | null = null;

  ngOnInit(): void {}

  editUser(index: number) {
    this.selectedUserIndex = index;
    this.editedUser = { ...this.newServis.users[index] };
  }

  updateUser(index: number) {
    if (this.selectedUserIndex === index) {
      this.newServis.users[index] = { ...this.editedUser };
      this.selectedUserIndex = null;
      this.editedUser = { name: '', phone: '', password: '' };
    }
  }

  deleteUser(index: number) {
    this.newServis.users.splice(index, 1);
  }
}
<h2>Users List</h2>
<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th>Password</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of newServis.users; let i = index;">
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>{{ user.password }}</td>
      <td>
        <button (click)="editUser(i)">Edit</button>
        <button (click)="updateUser(i)">Update</button>
        <button (click)="deleteUser(i)">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
