import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{


  editUser: User ={
    id:-1,
    name:"",
    phone:"",
    email :"",
  }

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }
  edit(user: User, i: number) {
    this.editUser = {
      id: user.id,
      name: user.name,
      phone: user.phone,
      email: user.email,
    };

    console.log("this-is-for-edit-method", user, this.editUser);

    this.newServis.users[i].editMode = true;
  }

  save(user: User) {
    console.log("this-is-for-save-method", user, this.editUser);
    const foundUser = this.newServis.users.find(usr => usr.id === user.id);
    if (foundUser) {
      foundUser.id = user.id;
      foundUser.name = this.editUser.name;
      foundUser.phone = this.editUser.phone;
      foundUser.email = this.editUser.email;
      foundUser.editMode = false;
    }
  }

  cancel(user:User){
    user.editMode=false;
  }

}
