@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent {

  currenItem = 'Television';
  items = [' '];

  addItem(newItem: string) {
    this.items.push(newItem);
  }
  deleteToo(i: number){
  this.items.splice(i,1)
  }

}
