import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';
import { Call } from '@angular/compiler';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{

  isSameUser=true
  editUser: User ={
    id:-1,
    name:"",
    phone:"",
    email :"",
  }

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }

  edit(user:User,i:number){
    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      email:user.email,}
    if(this.editUser.id===user.id){
       this.isSameUser=true
      }
    this.isSameUser=false
    this.newServis.users[i].editMode=true;
  }

 save(user:User,i:number){
  console.log(user,this.editUser);
  const indexExist = this.newServis.users.findIndex(x=>x.id===user.id) 
   if(indexExist === -1 ){
    this.newServis.users.push(this.editUser)
   }
   else
   this.delete(this.editUser,i)
      console.log("this.editUser: "+this.editUser.id);
  console.log("the array: "+this.newServis.users)
  // 





}

  cancel(user:User){
    user.editMode=false;
    this.isSameUser=true
  }


  delete(u:User,i:number){
    if(u.id===this.editUser.id){   
      if(this.newServis.users[i]===u){
        this.newServis.users.splice(i,1)
        this.isSameUser=true

      }
    }

 console.log(i,this.newServis.users)
  }

}
