import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {
  users: any[] = [];
  newUser: any = {};
  editingUser: any = null;

  ngOnInit(): void {
    this.users = [
      { name: 'User 1', phone: '1234567890', password: 'password1' },
      { name: 'User 2', phone: '9876543210', password: 'password2' },
      // ... רשימת המשתמשים הקיימים ...
    ];
  }

  addUser() {
    this.users.push(this.newUser);
    this.newUser = {};
  }

  editUser(user: any) {
    this.editingUser = user;
    // כאן תוכל לכתוב קוד לעיבוד המשתמש לעריכה
  }

  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
<h2>Users Form</h2>
<form (ngSubmit)="addUser(); userForm.reset()" #userForm="ngForm">
  <label for="name">Name:</label>
  <input type="text" id="name" name="name" [(ngModel)]="newUser.name" required>
  <br>
  <label for="phone">Phone:</label>
  <input type="text" id="phone" name="phone" [(ngModel)]="newUser.phone" required>
  <br>
  <label for="password">Password:</label>
  <input type="password" id="password" name="password" [(ngModel)]="newUser.password" required>
  <br>
  <button type="submit">Submit</button>
</form>

<app-shows [users]="users"></app-shows>

<h2>Users List</h2>
<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of users">
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>
        <button (click)="editUser(user)">Edit</button>
        <button (click)="deleteUser(user)">Delete</button>
      </td>
    </tr>
  </tbody>
</table>

<div *ngIf="editingUser">
  <h2>Edit User</h2>
  <form (ngSubmit)="updateUser(); editingUser = null">
    <label for="editName">Name:</label>
    <input type="text" id="editName" name="editName" [(ngModel)]="editingUser.name" required>
    <br>
    <label for="editPhone">Phone:</label>
    <input type="text" id="editPhone" name="editPhone" [(ngModel)]="editingUser.phone" required>
    <br>
    <label for="editPassword">Password:</label>
    <input type="password" id="editPassword" name="editPassword" [(ngModel)]="editingUser.password" required>
    <br>
    <button type="submit">Update</button>
  </form>
</div>
