import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }


 update(us:User){
const i = this.newServis.users.find(x=>x.name==us.name)
if(i){
this.newServis.users.push(us)
} 
}


}
<h2>Users List</h2>
<table>
  <thead>
    <tr>
      <th>Name</th>
      <th>Phone</th>
      <th>Password</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let user of newServis.users">
      <td>{{ user.name }}</td>
      <td>{{ user.phone }}</td>
      <td>{{ user.password }}</td>
      <td> action <button type="button" (click)="update(user)">update</button>
      </td>

    </tr>
  </tbody>
</table>
