import { Component, OnInit } from '@angular/core';
import { User } from '../userInterface';
import { FormGroup } from '@angular/forms';
import { FormControl } from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms';


@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {

  users: User[] = [

  ];

  newUser: User  ={
    name:"",
    phone:"",
    password:""
  };
  editingUser: User ={
    name:"",
    phone:"",
    password:""
  };
  mygroup !: FormGroup;
  ngOnInit() {
  this.mygroup = new FormGroup({
    name: new FormControl(),
    phone: new FormControl(),
    password: new FormControl()
  });

  }

  addUser() {
    console.log("this.newUser: ",this.newUser)
    this.users.push(this.newUser);

    console.log("this.users: ",this.users)
  }

  editUser(user: User) {
    this.editingUser = { ...user };
  }

  updateUser() {
    console.log("inupdate user")
    const updatedUserIndex = this.users.findIndex(user => user.name === this.editingUser.name);
    if (updatedUserIndex !== -1) {
      this.users[updatedUserIndex] = { ...this.editingUser };
    }

    this.editingUser = {
      name:"",
      phone:"",
      password:""
    }; // לאחר העדכון, לנקות את המשתנה המשמש לעריכה
  }

  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
