import { Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import { User } from '../userInterface';
import { ServiceService } from '../service.service';



@Component({
  selector: 'app-shows',
  templateUrl: './shows.component.html',
  styleUrls: ['./shows.component.css']
})
export class ShowsComponent implements OnInit{


  editUser: User ={
    id:-1,
    name:"",
    phone:"",
    email :"",
  }

  constructor(public newServis:ServiceService){}


  ngOnInit(): void {
  }

  edit(user:User,i:number){
    this.editUser = {
      id:user.id,
      name:user.name,
      phone:user.phone,
      email:user.email,
    }

    console.log("this-is-for-edit-method"+user,this.editUser);

    this.newServis.users[i].editMode=true;


  }

 save(user1:User){
  console.log("this-is-for-save-method"+user1,this.editUser);
  const founduser = this.newServis.users.find(usr=>usr.id===user1.id)
  if(founduser){
    this.editUser = {
      id:user1.id,
      name:user1.name,
      phone:user1.phone,
      email:user1.email,
      }
user1.editMode=false

  }    
  // user.editMode=false;
  }

  cancel(user:User){
    user.editMode=false;
  }

}
