import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit {
  users: any[] = [];
  newUser: any = {};
  editingUser: any = null;

  ngOnInit(): void {
    this.users = [

      // ... רשימת המשתמשים הקיימים ...
    ];
  }

  addUser() {
    this.users.push(this.newUser);
    this.newUser = {};
  }

  editUser(user: any) {
    this.editingUser = { ...user };
  }

  updateUser() {
    // ניתן להוסיף כאן את הלוגיקה לעדכון המשתמש ברשימת המשתמשים
    this.editingUser = null;
  }

  deleteUser(user: any) {
    const index = this.users.indexOf(user);
    if (index !== -1) {
      this.users.splice(index, 1);
    }
  }
}
<h2>Users Form</h2>
<form (ngSubmit)="addUser(); userForm.reset()" #userForm="ngForm">
  <div class="form-group">
    <label for="name">Name:</label>
    <input type="text" id="name" name="name" class="form-control" [(ngModel)]="newUser.name" required>
  </div>
  <div class="form-group">
    <label for="phone">Phone:</label>
    <input type="text" id="phone" name="phone" class="form-control" [(ngModel)]="newUser.phone" required>
  </div>
  <div class="form-group">
    <label for="password">Password:</label>
    <input type="password" id="password" name="password" class="form-control" [(ngModel)]="newUser.password" required>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>

<app-shows [users]="users"></app-shows>

<div *ngIf="editingUser">
  <h2>Edit User</h2>
  <form (ngSubmit)="updateUser(); editingUser = null">
    <div class="form-group">
      <label for="editName">Name:</label>
      <input type="text" id="editName" name="editName" class="form-control" [(ngModel)]="editingUser.name" required>
    </div>
    <div class="form-group">
      <label for="editPhone">Phone:</label>
      <input type="text" id="editPhone" name="editPhone" class="form-control" [(ngModel)]="editingUser.phone" required>
    </div>
    <div class="form-group">
      <label for="editPassword">Password:</label>
      <input type="password" id="editPassword" name="editPassword" class="form-control" [(ngModel)]="editingUser.password" required>
    </div>
    <button type="submit" class="btn btn-success">Update</button>
  </form>
</div>
